import React from "react";
import SkillIcon from "./SkillIcon";
import { skills } from "./Skills";

const SkillSet = () => {
  // A map of <skill_type, [skill_icons]>
  let iconMap = {
    "all": [],
    "game": [],
    "web": [],
    "data": [],
  };
  skills.forEach((skill, i) => {
    const icon = <SkillIcon key={i} src={skill.image} caption={skill.name} comment={skill.comment} />
    iconMap["all"].push(icon);
    skill.types.forEach(type => {
      iconMap[type].push(icon);
    });
  });

  return (
    <div className="skill-set-wrapper">
      <h2>My Perks</h2>
      {iconMap["all"]}
    </div>
  );
};

export default SkillSet;
