import React, { useState } from "react";
import { Card, Space, Tag } from "antd";
import ProjectDetail from "./ProjectDetail";
import "./ProjectCard.css";

const Meta = Card;

const COLOR_SCHEMES = [
  "#1E3163",
  "#2D46B9",
  "#F037A5",
  "#F8F8F8",
  "#3DB2FF",
  "#FFEDDA",
  "#FFB830",
  "#FF2442",
  "#6F69AC",
  "#95DAC1",
  "#FFEBA1",
  "#FD6F96",
];

const ProjectCard = ({ coverSrc, title, description, tags, detail }) => {
  const [isOverlayVisible, setIsOverlayVisible] = useState(false);

  const handleOnClick = () => {
    setIsOverlayVisible(true);
  };

  // Generate a random HTML color based on a given str as a key.
  // The same key will yield the same color.
  const generateRandomColor = str => {
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
      const c = str.charCodeAt(i);
      hash = (hash << 5) + hash + c;
    }
    const colorcode = COLOR_SCHEMES[Math.abs(hash) % COLOR_SCHEMES.length];
    return colorcode;
  };

  // Create a list of tag items given the project tag component list.
  const makeTagComponents = (tagItemList, outList) => {
    tagItemList.forEach((tagItem, index) => {
      const colorcode = generateRandomColor(tagItem);
      outList.push(
        <Tag key={index} color={colorcode}>
          {tagItem}
        </Tag>
      );
    });
  };

  let tagComponents = [];
  makeTagComponents(tags, tagComponents);

  return (
    <>
      <Card
        hoverable
        cover={<img className="cover-image" src={coverSrc} alt={title} />}
        onClick={handleOnClick}
      >
        <Meta title={title} bordered={false}>
          <p className="project-card-description">{description}</p>
          <div className="project-card-tags-wrapper">{tagComponents}</div>
        </Meta>
      </Card>
      <ProjectDetail
        isVisible={isOverlayVisible}
        setIsVisible={setIsOverlayVisible}
      />
    </>
  );
};

export default ProjectCard;
